import { useEffect, useState } from "react";
import { useQuery } from "react-query";
import axios from "axios";
import { INominatedMovie, NominatedMovieProps } from "../../components/NominatedMovie/NominatedMovie.types";
import { NominatedMovie } from "../../components/NominatedMovie/NominatedMovie";
import { useCookies } from "react-cookie";
export const Winner = () => {
  const [cookie] = useCookies(['movies']);
  const [winner, setWinner] = useState<INominatedMovie | null>(null);
  const { data: nominatedMovies, isLoading } = useQuery<INominatedMovie[]>(
    "nominated-movies",
    async () => {
      const response = await axios.get(
        "http://www.omdbapi.com/?i=tt3896198&apikey=3d85689c"
      );
      console.log(response)
      return response.data.Search;
    }
  );

  useEffect(() => {
    if (nominatedMovies && nominatedMovies.length > 0) {
      const randomIndex = Math.floor(Math.random() * nominatedMovies.length);
      setWinner(nominatedMovies[randomIndex]);
    }
  }, [nominatedMovies]);

  useEffect(() => {
    console.log(cookie.movies)
  }, [cookie]);

  return isLoading ? (
    <p>Loading winner...</p>
  ) : (
    <>
      {winner ? (
        <div>
          <h1>Winner</h1>
          <NominatedMovie
            id={winner.imdbID}
            index={1} // You can set the desired index here
            onRemove={() => {}} // You can add a remove handler if needed
          />
        </div>
      ) : (
        <p>No nominated movies found.</p>
      )}
    </>
  );
};

