import React from "react";
import { NominatedMovie } from "../../components/NominatedMovie/NominatedMovie";
import "./Winner.css";
import { WinnerProps } from "./Winner.types";

export const Winner = ({ movies }: WinnerProps) => {
  const handleSelectWinner = () => {
    // Randomly select a movie as the winner
    const randomIndex = Math.floor(Math.random() * movies.length);
    const winnerMovie = movies[randomIndex];
    console.log("Winner:", winnerMovie);
  };

  return (
    <div className="winner-container">
      <h1>Winner</h1>
      <button onClick={handleSelectWinner}>Select Winner</button>
      <div className="nominated-movies-container">
        {movies.map((movie, index) => (
          <NominatedMovie
            key={movie.id}
            id={movie.id}
            index={index + 1}
            onRemove={() => {}}
          />
        ))}
      </div>
    </div>
  );
};
